    //         const json = await res.json();
    //         setUsers(json);
    //         console.log(json);
    //     };
    //     fetchUsers();
    // }, []);

    let renderUsers = data.map((user) => {
        return (
            <div key={user.id}>
                <p>
                    <strong>{user.name}</strong>
                </p>
            </div>
        )
    });

    // HOC replaces
