    //     return name.toLowerCase().indexOf(term) >= 0;
    // }).map((user) => {
    //     return (
    //         <div key={user.id}>
    //             <p>
    //                 <strong>{user.name}</strong>
    //             </p>
    //         </div>
    //     )
    // });

    return (
        <div>
            <div>{renderUsers}</div>
        </div>
    );
};

// when passing into HOC this will return a new component
const SearchUsers = HOC(UsersList, "users");

export default SearchUsers;
